import './App.css';
import NavBar from './components/NavBar';
import TestimonalCard from './components/TestimonialSlider';
import Slider from "react-slick"
import testimonials from './components/testimonials';

function cards(testimonials) {
  return (
    <TestimonalCard
      id={testimonials.id}
      name={testimonials.name}
      message={testimonials.message}
    />
  )
}

const NextBtn = (props) => {
  const { className, onClick } = props;
  return (
    <div className={className} onClick={onClick}>
      <props.icon style={{ color: "#994d47", fontSize: "30px" }} />
    </div>
  );
};

const PreviousBtn = (props) => {
  const { className, onClick } = props;
  return (
    <div className={className} onClick={onClick}>
      <props.icon style={{ color: "#994d47", fontSize: "30px" }} />
    </div>
  );
};

function App() {
  const settings = {
    dots: true,
    fade: true,
    autoplay: true,
    infinite: true,
    speed: 500,
    autoplayspeed: 5000,
    slidestoShow: 1,
    SlidestoScroll: 1

  }
  return (
    <div>
      <NavBar />
      <div>
        <h1>Testimonials</h1>
        <Slider {...settings}>{testimonials.map(cards)}</Slider>
      </div>
    </div>
  );
}

export default App;
